public class Loops {
    
    public static void main(String[] args) {
        // TODO Auto-generated method stub
        //for loop
        for (int i = 1; i <= 10; i++) {
            System.out.println("number: " + i);
        }
        //while loop
        int i = 1;
        while (i <= 10) {
            System.out.println("number: " + i);
            i++;
        }
        //do while loop
        int j = 1;
        do {
            System.out.println("number: " + j);
            j++;
        } while (j <= 10);
    }

}
